import React, { useEffect,useState } from 'react'
import {user} from '../Join/Join';
import socketIo from 'socket.io-client';
import './Chat.css';
import sendLogo from '../../../src/download.png';
import Message from '../Message/Message';

import ReactScrolltoBottom from 'react-scroll-to-bottom';



const ENDPOINT="http://localhost:4500";
let socket;

const Chat = () => {
    const[messages,setmessage]=useState([]);
    const[id,setid]=useState("");

    const send=()=>{
        const message= document.getElementById('chatInput').value;
        socket.emit('message',{message,id});
        document.getElementById('chatInput').value="";

    }



    useEffect(()=>{

        socket = socketIo(ENDPOINT,{transports:['websocket']})
        socket.on("connect",()=>{
            alert("connected");
            setid(socket.id);
        })

        socket.emit('joined',{user})//event name is joined and emit means sending data to backend
        socket.on('welcome',(data)=>{
            setmessage([...messages,data])
            console.log(data.user,data.message);

        })

        socket.on('userjoined',(data)=>{
            console.log(data.user,data.message);
        })

        socket.on('leave',(data)=>{
            console.log(data.user,data.message);
        })
        return ()=>{
            socket.emit('disconnet')
            socket.off();

        }   

    },[])

    useEffect(()=>{
        socket.on('sendMessage',(data)=>{
                console.log(data.user,data.message,data.id);
        })
    },[])
  return (
    <div className='chatpage'>
        <div className='chatContainer'>
        <div className='header'></div>
        <ReactScrolltoBottom className='chatbox'>
            {/* <Message message={"Hi there son"} />
            <Message message={"Hi there son"} />
            <Message message={"Hi there son"} /> */}

            {messages.map((items,i)=><Message message={items}/>)}

        </ReactScrolltoBottom>

        <div className='inputbox'>
           <input type="text" id='chatInput'/>
           <button onClick={send}   className='sendBtn'>{<img src={sendLogo}/>}</button>

        </div>

        </div>



    </div>
  )
}

export default Chat
